Program.cs<pre><code>using Microsoft.AspNetCore.Components.Web;
using Microsoft.AspNetCore.Components.WebAssembly.Hosting;
using MudBlazor.Extensions;

var builder = WebAssemblyHostBuilder.CreateDefault(args);
builder.RootComponents.Add&lt;App&gt;("#app");
builder.RootComponents.Add&lt;HeadOutlet&gt;("head::after");

builder.Services.AddScoped(sp =&gt; 
    new HttpClient { BaseAddress = new Uri(builder.HostEnvironment.BaseAddress) });

// Register MudBlazor and MudBlazor.Extensions
builder.Services.AddMudServicesWithExtensions(c =&gt;
{
    c.WithDefaultDialogOptions(ex =&gt;
    {
        ex.CloseButton = true;
        ex.MaxWidth = MaxWidth.Medium;
    });
});

await builder.Build().RunAsync();
</code></pre>

Program.cs<pre><code>using MudBlazor.Extensions;

var builder = WebApplication.CreateBuilder(args);

// Add services to the container
builder.Services.AddRazorPages();
builder.Services.AddServerSideBlazor();

// Register MudBlazor and MudBlazor.Extensions
builder.Services.AddMudServicesWithExtensions();

var app = builder.Build();

// Configure the HTTP request pipeline
if (!app.Environment.IsDevelopment())
{
    app.UseExceptionHandler("/Error");
    app.UseHsts();
}

app.UseHttpsRedirection();
app.UseStaticFiles();
app.UseRouting();

// Add MudEx middleware
app.Use(MudExWebApp.MudExMiddleware);

app.MapBlazorHub();
app.MapFallbackToPage("/_Host");

app.Run();
</code></pre>
